
public class Diamondpattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int num =1;
		
		int n =3;
		
		
		for (int i =0 ;i<=n;i++)
		{
			
			for (int j= 0;j<=i;j++)
			{
				
				System.out.print(num);
				num++;
			}
			System.out.println();
			
		}
		
		
		

	}

}
