package naveen;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 27 ;
		
		int fact =1;
		
		for (int i=0 ;i<=num;i++)
		{
		fact = fact*i;
		
		
			
		}
		System.out.println("the factorial of the number "+ num + "  is: "  + fact );
	}

}
