package practiceprograms;

public class Starpattern123456 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n = 1;
		
		for (int i =0; i<4;i++)
		{
			
			for(int j =0;j<i;j++)
			{
				
				System.out.print(n+ " ");
				n++;
				
			}
			System.out.println("");
			
			
			
		}
		
		
		

	}

}
